import { useEffect, useState } from 'react';
import './App.css';

function App() {
  const [number, MyNumber] = useState(0);
  const [num,setNum]=useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      MyNumber(prevNumber => prevNumber + 1);
    }, 1000); // Increase the number every second (1000 milliseconds)

    // Clean up the interval when the component unmounts
    return () => clearInterval(interval);
  }, []); // Empty dependency array ensures the effect runs only once

  const Increase=()=>{
    setNum(num+1);
  }
  const Desc=()=>{
    if(num>0){
      setNum(num-1);
    }
  }

  return (
    <>
       <div className='border p-6'>
      <h1>Number: {number}</h1>
      <h1 className='text-green-400 text-2xl font-bold'>Number you want to add or remove: {num}</h1>

          <button className='bg-yellow-400 p-2 shadow-xl  rounded-lg text-xl' onClick={Increase}>+</button><button onClick={Desc
          }>-</button>
        </div>
    </>
  );
}

export default App;
